import React from 'react';
import './App.css';
import { observer } from 'mobx-react';
import taskStore, { Task } from './TaskStore'; // Import Task interface and taskStore



const App: React.FC = observer(() => {
  const { tasks, addTask, toggleTask, removeTask, newTask, isListLayout, setNewTask, setIsListLayout } = taskStore;

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNewTask(event.target.value);
  };

  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      addTask({
        id: String(Date.now()),
        title: newTask,
        completed: false,
        createDate: new Date(),
      });
      setNewTask('');
    }
  };

  return (
    <div className="App">
      <nav>
        <p>Almost there: {tasks.filter(task => task.completed).length / tasks.length * 100}% done</p>
        <button onClick={() => setIsListLayout(!isListLayout)}>
          {isListLayout ? 'Switch to Tiles' : 'Switch to List'}
        </button>
      </nav>
      <div>
        <input
          type="text"
          placeholder="Enter task"
          value={newTask}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress} // Use handleKeyPress for onKeyPress event
        />

      </div>
      {isListLayout ? (
        <ul>
          {tasks.map((task) => (
            <li key={task.id} className="task-item">
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span className={task.completed ? 'completed' : ''}>{task.title}</span>
              <button className="remove-button" onClick={() => removeTask(task.id)}>
                Remove
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <div className="tiles-container">
          {tasks.map((task) => (
            <div key={task.id} className={`task-item tile ${task.completed ? 'completed' : ''}`}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span>{task.title}</span>
              <button className="remove-button" onClick={() => removeTask(task.id)}>
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
});

export default App;
