import React, { useState } from 'react';
import { observer } from 'mobx-react';
import { observable, action } from 'mobx';
import './App.css';

interface Task {
  id: string;
  title: string;
  completed: boolean;
  createDate: Date;
}

class TaskStore {
  @observable tasks: Task[] = [];

  @action
  addTask(title: string) {
    if (title.trim() !== '') {
      const newTaskItem = {
        id: String(Date.now()),
        title: title,
        completed: false,
        createDate: new Date(),
      };
      this.tasks.push(newTaskItem);
    }
  }

  @action
  toggleTask(taskId: string) {
    const task = this.tasks.find(task => task.id === taskId);
    if (task) {
      task.completed = !task.completed;
    }
  }

  @action
  removeTask(taskId: string) {
    this.tasks = this.tasks.filter(task => task.id !== taskId);
  }
}

const taskStore = new TaskStore();

const App = observer(() => {
  const [newTask, setNewTask] = useState('');

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNewTask(event.target.value);
  };

  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      taskStore.addTask(newTask);
      setNewTask('');
    }
  };

  const finishedTasks = tasks.filter((task) => task.completed).length;
  const percentageCompleted = tasks.length > 0 ? (finishedTasks / tasks.length) * 100 : 0;

  return (
    <div className="App">
      <nav>
        <p>Almost there: {percentageCompleted.toFixed(2)}% done</p>
        <button onClick={() => setIsListLayout(!isListLayout)}>
          {isListLayout ? 'Switch to Tiles' : 'Switch to List'}
        </button>
      </nav>
      <div>
        <input
          type="text"
          placeholder="Enter task"
          value={newTask}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <button onClick={addTask}>Add Task</button>
      </div>
      {isListLayout ? (
        <ul>
          {tasks.map((task) => (
            <li key={task.id} className="task-item">
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span className={task.completed ? 'completed' : ''}>{task.title}</span>
              <button className="remove-button" onClick={() => removeTask(task.id)}>
                Remove
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <div className="tiles-container">
          {tasks.map((task) => (
            <div key={task.id} className={`task-item tile ${task.completed ? 'completed' : ''}`}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span>{task.title}</span>
              <button className="remove-button" onClick={() => removeTask(task.id)}>
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
});

export default App;
